#34; # Variable dump dump_vars() { local var for var in &#34;$@&#34;; do echo &#34;$var = ${!var:-&lt;undefined&gt;}&#34; done } dump_vars USER HOME PATH # Stack trace on error print_stack_trace() { local i echo &#34;Stack trace:&#34; for ((i=1; i&lt;${#FUNCNAME[@]}; i++)); do echo &#34; [$i] ${FUNCNAME[$i]}() at ${BASH_SOURCE[$i]}:${BASH_LINENO[$i-1]}&#34; done } trap &#39;print_stack_trace&#39; ERR8.4 Common Issues &amp; Debug Tips # Issue: Quoting problems # Bad: files=$(ls *.txt) for f in $files; do # Breaks on filenames with spaces # Good: for f in *.txt; do # Issue: Variable scope in subshell # Bad: echo &#34;test&#34; | while read line; do result=&#34;$line&#34; done echo &#34;$result&#34; # Empty! # Good: while read line; do result=&#34;$line&#34; done &lt; &lt;(echo &#34;test&#34;) echo &#34;$result&#34; # Works # Issue: Checking command success # Bad: if [ $(grep -c &#34;pattern&#34; file) -gt 0 ]; then # Good: if grep -q &#34;pattern&#34; file; then # Debug tip: Print vá»›i colors RED=&#39;\033[0;31m&#39; GREEN=&#39;\033[0;32m&#39; NC=&#39;\033[0m&#39; echo -e &#34;${RED}Error${NC}: something wrong&#34; echo -e &#34;${GREEN}OK${NC}: passed&#34; 9. ARGUMENT PARSING - GETOPTS 9.1 getopts cÆ¡ báº£n #!/usr/bin/env bash usage() { echo &#34;Usage: $0 [-v] [-f file] [-n number] args...&#34; echo &#34;Options:&#34; echo &#34; -v Verbose mode&#34; echo &#34; -f FILE Input file&#34; echo &#34; -n NUMBER Count&#34; echo &#34; -h Show help&#34; exit 1 } # Default values VERBOSE=false FILE=&#34;&#34; COUNT=1 # Parse options while getopts &#34;:vf:n:h&#34; opt; do case $opt in v) VERBOSE=true ;; f) FILE=&#34;$OPTARG&#34; ;; n) COUNT=&#34;$OPTARG&#34; ;; h) usage ;; :) echo &#34;Option -$OPTARG requires argument&#34; &gt;&amp;2; exit 1 ;; \?) echo &#34;Invalid option: -$OPTARG&#34; &gt;&amp;2; exit 1 ;; esac done # Remove parsed options, $@ chá»©a remaining arguments shift $((OPTIND - 1)) echo &#34;VERBOSE: $VERBOSE&#34; echo &#34;FILE: $FILE&#34; echo &#34;COUNT: $COUN